import React, {Component} from 'react';
import axios from 'axios';
import {Table} from 'reactstrap';

export default class Mounts extends Component{
	constructor(props){
		super(props);
		this.state={
			data:[]
		}
		this.convertArray=this.convertArray.bind(this);		
	}
  convertArray=(obj) =>{
    const item=[];
    Object.keys(obj).forEach(function(key) {
      item.push(obj[key]);
    });
    return item[0]['mounts'];
  };

	componentDidMount(){
		axios.get(`https://us.api.blizzard.com/wow/mount/?locale=en_US&access_token=${process.env.REACT_APP_WOW_API_KEY}`)
		.then((res)=>{
			console.log(this.convertArray(res))
			this.setState({data:this.convertArray(res)})
		})
		.catch((err)=>{
			console.log(err)
		})
	}

	render(){
		const parsedData=this.state.data.map((k,i)=> {
