import React,{Component} from 'react';
import {Table} from 'reactstrap';

export default class sharedTable extends Component{
	constructor(props){
		super(props);
		this.convertArray=this.convertArray.bind(this);
	}
  convertArray=(obj,type) =>{
    const item=[];
    Object.keys(obj).forEach(function(key) {
      item.push(obj[key]);
    });
    return item[0][type];
  };
	render(){
		const header=the.props.header.map((k)=>{
			return(
				<th>{k}</th>
			)
		})
		const data=this.convertArray(this.props.data,this.props.type)
		const parsedData=data.map((k,i)=> {
			return(
				<tr>
					<td>{k.name}</td>
					<td>{k.icon}</td>
					<td>{(k.isGround && k.isFlying) ? 'Flying & Ground Type' : k.isGround ? 'Ground Type' : k.isFlying ? 'Flying Type' : 'None'}</td>
				</tr>
			)
		})
		return(	
			<Table>
				<thead>
					{props.header}
				</thead>
				<tbody>
					{props.data}
				</tbody>
			</Table>
		)
	}
}
