import React, {Component} from 'react';
import axios from 'axios';
import sharedTable from './sharedTable';

export default class Mounts extends Component{
	constructor(props){
		super(props);
		this.state={
			data:[]
		}
		this.convertArray=this.convertArray.bind(this);		
	}


	componentDidMount(){
		axios.get(`https://us.api.blizzard.com/wow/mount/?locale=en_US&access_token=${process.env.REACT_APP_WOW_API_KEY}`)
		.then((res)=>{
			// console.log(this.convertArray(res))
			this.setState({data:this.convertArray(res)})
		})
		.catch((err)=>{
			console.log(err)
		})
	}

	render(){
		const headerNames=['Name','Icon','Type'];
		const header=headerNames.map((k)=>{
			return(
				<thead>
					<th>{k}</th>
				</thead>
			)
		})
		const parsedData=this.state.data.map((k,i)=> {
			return(
				<tbody>
					<tr>
						<td>{k.name}</td>
						<td>{k.icon}</td>
						<td>{(k.isGround && k.isFlying) ? 'Flying & Ground Type' : k.isGround ? 'Ground Type' : k.isFlying ? 'Flying Type' : 'None'}</td>
					</tr>
				</tbody>
			)
		})

		return(
			<sharedTable header={headerNames} data={this.state.data} />
		)
	}
}
