import React, {Component} from 'react';
import axios from 'axios';
import {Table} from 'reactstrap';

var API_KEY='';
export default class Bosses extends Component{
	constructor(props){
		super(props);
		this.state={
			data:[]
		}
		this.convertArray = this.convertArray.bind(this);
	}
  convertArray=(obj) =>{
    const item=[];
    Object.keys(obj).forEach(function(key) {
      item.push(obj[key]);
    });
    return item[0];
  };

	componentWillMount(){
		API_KEY=process.env.REACT_APP_WOW_API_KEY
	}
	componentDidMount(){
		axios.get(`https://us.api.blizzard.com/wow/boss/?locale=en_US&access_token=${API_KEY}`)
		.then((res)=>{
			console.log(this.convertArray(res))
			this.setState({data:this.convertArray(res)})
		})
		.catch((err)=>{
			console.log(err)
		})
	}
	
	render(){
		return(
			<Table>
				<thead>
					<th>Name</th>
					<th>Health</th>
					<th>Level</th>
					<th>Description</th>
				</thead>
				<tbody>
					{this.state.data.map((k)=>{
						<td>{k.name}</td>
					})}
				</tbody>
			</Table>
		)
	}
}
