import React, {Component} from 'react';
import axios from 'axios';
import {Table} from 'reactstrap';

export default class Characters extends Component{
	constructor(props){
		super(props);
		this.state={data:[]}
		this.convertArray=this.convertArray.bind(this);
	}
  convertArray=(obj) =>{
    const item=[];
    Object.keys(obj).forEach(function(key) {
      item.push(obj[key]);
    });
    return item[0];
  };
	componentDidMount(){
		axios.get(`https://us.api.blizzard.com/data/wow/race/index?namespace=static-us&locale=en_US&access_token=${process.env.REACT_APP_WOW_API_KEY}`)
		.then((res)=>{
			console.log(this.convertArray(res))
			this.setState({data:this.convertArray(res)})
		})
	}
	render(){
		return(
			<Table>

			</Table>
		)
	}
}
