import React, {Component} from 'react';
import axios from 'axios';
import {Col, Row} from 'reactstrap';

export default class Profile extends Component{
	constructor(props){
		super(props);
		this.state={data:[]}
	}
	componentDidMount(){
		axios.get(`https://us.api.blizzard.com/wow/character/tichondrius/Diglover?locale=en_US&access_token=${process.env.REACT_APP_WOW_API_KEY}`)
		.then((res)=>{
			console.log(res)
			this.setState({data:res['data']})
		})
		.catch((err)=>{
			console.log(err)
		})
	}
	render(){
		const profileImg=(k)=> {
			return <img src={`http://render-us.worldofwarcraft.com/character/${k}`} />
		}
		const replaceStr=(k)=> {
			var main=k.replace('avatar','main')
			return main
		}
		return(
			<Row>
				<Col xs='3'>{profileImg(replaceStr(this.state.data.thumbnail))}</Col>
				<Col xs='9'></Col>
			</Row>
		)
	}
}
