import React, {Component} from 'react';
import axios from 'axios';
import {Table} from 'reactstrap';
import SharedTable from './sharedTable';

export default class Characters extends Component{
	constructor(props){
		super(props);
		this.state={data:[]}

	}

	componentDidMount(){
		axios.get(`https://us.api.blizzard.com/wow/data/character/races?locale=en_US&access_token=${process.env.REACT_APP_WOW_API_KEY}`)
		.then((res)=>{
			console.log(res.data.races)
			this.setState({data:res.data.races})
		})
		.catch((err)=>{
			console.log(err)
		})
	};


	render(){
		const charImg=(i,s)=>{
			return <img src={`https://render-us.worldofwarcraft.com/character/proudmoore/144/12345-main.jpg?alt=/wow/static/images/2d/avatar/${i}-${s}.jpg`} />
		}
		var headerNames=['Name','Faction','Image']
		var tdValues=['name','side']
		return(
			<SharedTable header={headerNames} dataValue={this.state.data} tdValues={tdValues} charImg={charImg} />
		)
	}
}
