import React, {Component} from 'react';
import axios from 'axios';
import {Col, Row, Table} from 'reactstrap';
import SharedTable from './sharedTable';
export default class Profile extends Component{
	constructor(props){
		super(props);
		this.state={data:[]}
	}
	componentDidMount(){
		axios.get(`https://us.api.blizzard.com/wow/character/tichondrius/Diglover?locale=en_US&access_token=${process.env.REACT_APP_WOW_API_KEY}`)
		.then((res)=>{
			console.log(res)
			this.setState({data:res['data']})
		})
		.catch((err)=>{
			console.log(err)
		})
	}


	render(){

		const profileImg=(k)=> {
			return <img src={`http://render-us.worldofwarcraft.com/character/${k}`} />
		}
		const headerNames=['Name','Realm','Achievement Points', 'Honorable Kills','Battle Group'];
		const tdValues=['name','realm','achievementPoints','totalHonorableKills','battlegroup']
		return(
			<SharedTable header={headerNames} tdValues={tdValues} type='profile' profileImg={profileImg} />

		)
	}
}
