import React, {Component} from 'react';
import axios from 'axios';
import SharedTable from './sharedTable';

export default class Mounts extends Component{
	constructor(props){
		super(props);
		this.state={
			data:[]
		}
		this.convertArray=this.convertArray.bind(this);
	}

	componentDidMount(){
		axios.get(`https://us.api.blizzard.com/wow/mount/?locale=en_US&access_token=${process.env.REACT_APP_WOW_API_KEY}`)
		.then((res)=>{
			this.setState({data:res.data.mounts})
		})
		.catch((err)=>{
			console.log(err)
		})
	}



	render(){
		const headerNames=['Name','Icon','Type'];
		const tdValues=['name','icon','isGround','isFlying'];
		const mounts=this.convertArray(this.state.data);
		return(
			<SharedTable header={headerNames} dataValue={mounts} tdValues={tdValues} />
		)
	}
}
