import React, {Component} from 'react';
import axios from 'axios';
import {Col, Row} from 'reactstrap';

export default class Profile extends Component{
	constructor(props){
		super(props);
		this.state={data:[]}
		this.replaceStr=this.replaceStr.bind(this);
	}
	componentDidMount(){
		axios.get(`https://us.api.blizzard.com/wow/character/tichondrius/Diglover?locale=en_US&access_token=${process.env.REACT_APP_WOW_API_KEY}`)
		.then((res)=>{
			console.log(res)
			this.setState({data:res['data']})
		})
		.catch((err)=>{
			console.log(err)
		})
	}
	replaceStr=(k)=> {
		var newStr=k.replace('avatar','');
		console.log(newStr)
	}
	render(){
		const replacedImg=this.replaceStr(this.state.data.thumbnail);
		const profileImg=(k)=> {
			return <img src={`http://render-us.worldofwarcraft.com/character/${k}`} />
		}
		return(
			<Row>
				<Col xs='3'>{profileImg(this.state.data.thumbnail)}</Col>
				<Col xs='9'></Col>
			</Row>
		)
	}
}
