import React, {Component} from 'react';
import axios from 'axios';
import {Table} from 'reactstrap';

export default class Characters extends Component{
	constructor(props){
		super(props);
		this.state={data:[]}
		this.convertArray=this.convertArray.bind(this);
	}
  convertArray=(obj) =>{
    const item=[];
    Object.keys(obj).forEach(function(key) {
      item.push(obj[key]);
    });
    return item[0]['races'];
  };
	componentDidMount(){
		axios.get(`https://us.api.blizzard.com/wow/data/character/races?locale=en_US&access_token=${process.env.REACT_APP_WOW_API_KEY}`)
		.then((res)=>{
			console.log(this.convertArray(res))
			this.setState({data:this.convertArray(res)})
		})
		.catch((err)=>{
			console.log(err)
		})
	};


	render(){
		const charImg=(i,s)=>{
			return <img src={`https://render-us.worldofwarcraft.com/character/proudmoore/144/12345-main.jpg?alt=/wow/static/images/2d/avatar/${i}-${s}.jpg`} />
		}
		const data=this.state.data;
		const dataName=data.map((k,i)=>{
			return(
				<tr>
					<td>{k.name}</td>
					<td>{k.side}</td>
					<td>{charImg(i,0)}<br/>
							{charImg(i+1,1)}
					</td>
				</tr>
			) 
		})
		
		return(
			<Table>
				<thead>
					<th>Name</th>
					<th>Faction</th>
					<th>Image</th>
				</thead>
				<tbody>
					{dataName}
				</tbody>
			</Table>
		)
	}
}
